function saveScore(e){let o=document.cookie.slice(4,-1),t=new Date;if(t.setFullYear(t.getFullYear()+1),o){let i=o.split(",");for(let o=0;o<i.length;o++)0!=e&&e>i[o]&&([e,i[o]]=[i[o],e]);document.cookie=`HS=[${i[0]},${i[1]},${i[2]},${i[3]}];expires=${t}`}else document.cookie=`HS=[${e},0,0,0];expires=${t}`}function writeHS(e){let o=document.cookie.slice(4,-1).split(","),t="";o.forEach((e=>{t+=`<li>${e}</li>`})),document.querySelector(e).innerHTML=t}